/**
 * Extends Jest's matcher interface with DOM-specific matchers
 * These matchers are used in unit tests to verify DOM elements
 */
declare global {
  namespace jest {
    interface Matchers<R> {
      // Checks if an element is present in the document
      toBeInTheDocument(): R;
      // Checks if an element has a specific CSS class
      toHaveClass(className: string): R;
    }
  }
}

